mapper.insert:
  	INSERT INTO PATIENT
  	(
		NO
		,PA_NAME
		,PA_TEL
		,RRN
		,PA_GENDER
		,CAREGIVER
		,ADDRESS
		,MEMO
  	)
  	VALUES
  	(
  		SEQ_PATIENT_NO.NEXTVAL
		, #{paName}
		, #{paTel}
		, #{rrn}
		, #{paGender}
		, #{caregiver}
		, #{address}
		, #{memo}
  	)

mapper.select:
SELECT R.NO ,P.PA_NAME ,P.PA_GENDER ,R.DEPT_NO ,R.SYMPTOM ,R.EMP_NO ,trunc((to_number(to_char(sysdate, 'YYYYMMDD'))-to_number(to_char(decode(substr(P.RRN, 8, 1), '1', '19', '2', '19', '20')||substr(P.RRN, 1, 6))))/10000) as "AGE" FROM PATIENT P JOIN RECEIPT R ON P.NO = R.PA_NO WHERE R.STATUS = 'W' AND
R.DEPT_NO IN (40, 50, 60, 70)
R.DEPT_NO = #{deptNo}

SELECT R.NO ,P.PA_NAME ,P.PA_GENDER ,R.DEPT_NO ,R.SYMPTOM ,R.EMP_NO ,trunc((to_number(to_char(sysdate, 'YYYYMMDD'))-to_number(to_char(decode(substr(P.RRN, 8, 1), '1', '19', '2', '19', '20')||substr(P.RRN, 1, 6))))/10000) as "AGE" FROM PATIENT P JOIN RECEIPT R ON P.NO = R.PA_NO WHERE R.STATUS = 'S' AND
R.DEPT_NO IN (40, 50, 60, 70)
R.DEPT_NO = #{deptNo}
